    ("seq"."seqmin")::"information_schema"."character_data" AS "identity_minimum",
    (
        CASE
            WHEN "seq"."seqcycle" THEN 'YES'::"text"
            ELSE 'NO'::"text"
        END)::"information_schema"."yes_or_no" AS "identity_cycle",
