            ELSE 'NO'::"text"
        END)::"information_schema"."yes_or_no" AS "is_updatable"
   FROM (((((("pg_attribute" "a"
     LEFT JOIN "pg_attrdef" "ad" ON ((("a"."attrelid" = "ad"."adrelid") AND ("a"."attnum" = "ad"."adnum"))))
     JOIN ("pg_class" "c"
     JOIN "pg_namespace" "nc" ON (("c"."relnamespace" = "nc"."oid"))) ON (("a"."attrelid" = "c"."oid")))
     JOIN ("pg_type" "t"
