        CASE
            WHEN ("nc"."oid" = "pg_my_temp_schema"()) THEN 'LOCAL TEMPORARY'::"text"
            WHEN ("c"."relkind" = ANY (ARRAY['r'::"char", 'p'::"char"])) THEN 'BASE TABLE'::"text"
            WHEN ("c"."relkind" = 'v'::"char") THEN 'VIEW'::"text"
