CREATE OR REPLACE VIEW "pg_catalog"."pg_stats_ext_exprs" WITH ("security_barrier"='true') AS
 SELECT "cn"."nspname" AS "schemaname",
    "c"."relname" AS "tablename",
    "sn"."nspname" AS "statistics_schemaname",
