        END AS "most_common_vals",
        CASE
            WHEN (("stat"."a")."stakind1" = 1) THEN ("stat"."a")."stanumbers1"
            WHEN (("stat"."a")."stakind2" = 1) THEN ("stat"."a")."stanumbers2"
