        END AS "most_common_freqs",
        CASE
            WHEN (("stat"."a")."stakind1" = 2) THEN ("stat"."a")."stavalues1"
            WHEN (("stat"."a")."stakind2" = 2) THEN ("stat"."a")."stavalues2"
            WHEN (("stat"."a")."stakind3" = 2) THEN ("stat"."a")."stavalues3"
